import React, { useEffect, useState } from "react";
import About from "./About";
import {
  LineChart, Line, XAxis, YAxis, CartesianGrid, Tooltip, Legend,
  RadialBarChart, RadialBar,
  AreaChart, Area,
  PieChart, Pie, Cell,
} from "recharts";

function App() {
  const [latest, setLatest] = useState({ temperature: 0, humidity: 0 });
  const [history, setHistory] = useState([]);
  const [error, setError] = useState(null);

  // Fetch latest data every 5 seconds
  useEffect(() => {
    const fetchLatest = async () => {
      try {
        const res = await fetch("http://localhost:8000/device/latest");
        if (!res.ok) throw new Error("Network response was not ok");
        const json = await res.json();
        setLatest(json);
        setError(null);
      } catch (err) {
        setError("Failed to fetch latest data");
        console.error(err);
      }
    };
    fetchLatest();
    const interval = setInterval(fetchLatest, 5000);
    return () => clearInterval(interval);
  }, []);

  // Fetch history once on mount and every 15 seconds
  useEffect(() => {
    const fetchHistory = async () => {
      try {
        const res = await fetch("http://localhost:8000/device/history");
        if (!res.ok) throw new Error("Network response was not ok");
        const json = await res.json();
        // Add time keys for chart (simulate timestamps)
        const now = new Date();
        const dataWithTime = json.map((item, idx) => {
          const time = new Date(now - (json.length - idx - 1) * 5000);
          return { ...item, time: time.toLocaleTimeString() };
        });
        setHistory(dataWithTime);
        setError(null);
      } catch (err) {
        setError("Failed to fetch history data");
        console.error(err);
      }
    };
    fetchHistory();
    const interval = setInterval(fetchHistory, 15000);
    return () => clearInterval(interval);
  }, []);

  const pieData = [
    { name: "Temperature", value: latest.temperature || 0, fill: "#8884d8" },
    { name: "Humidity", value: latest.humidity || 0, fill: "#82ca9d" },
  ];

  return (
    <>
      <About />
      <div style={{ padding: 30, fontFamily: "Arial", maxWidth: 900, margin: "auto" }}>
        <h2>Live IoT Dashboard</h2>
        {error ? (
          <p style={{ color: "red" }}>{error}</p>
        ) : (
          <>
            <p><strong>Temperature:</strong> {latest.temperature} Â°C</p>
            <p><strong>Humidity:</strong> {latest.humidity} %</p>

            <h3>Temperature & Humidity Trends (Last ~1.5 mins)</h3>
            <LineChart
              width={850}
              height={300}
              data={history}
              margin={{ top: 5, right: 30, left: 20, bottom: 5 }}
            >
              <CartesianGrid strokeDasharray="3 3" />
              <XAxis dataKey="time" />
              <YAxis />
              <Tooltip />
              <Legend />
              <Line type="monotone" dataKey="temperature" stroke="#8884d8" />
              <Line type="monotone" dataKey="humidity" stroke="#82ca9d" />
            </LineChart>

            <h3>Current Levels Gauge</h3>
            <RadialBarChart
              width={400}
              height={250}
              cx="50%"
              cy="50%"
              innerRadius="20%"
              outerRadius="90%"
              barSize={15}
              data={pieData}
              startAngle={180}
              endAngle={0}
            >
              <RadialBar minAngle={15} label background clockWise dataKey="value" />
              <Legend
                iconSize={10}
                width={120}
                height={40}
                layout="horizontal"
                verticalAlign="bottom"
                align="center"
              />
              <Tooltip />
            </RadialBarChart>

            <h3>Snapshot: Temperature vs Humidity</h3>
            <PieChart width={400} height={300}>
              <Pie
                data={pieData}
                dataKey="value"
                nameKey="name"
                cx="50%"
                cy="50%"
                outerRadius={100}
                label
              >
                {pieData.map((entry, index) => (
                  <Cell key={`cell-${index}`} fill={entry.fill} />
                ))}
              </Pie>
              <Tooltip />
              <Legend />
            </PieChart>
          </>
        )}
      </div>
    </>
  );
}

export default App;
