import React, { useEffect, useState } from "react";
import About from "./About";
import {
  LineChart, Line, XAxis, YAxis, CartesianGrid, Tooltip, Legend,
  RadialBarChart, RadialBar,
  AreaChart, Area,
  PieChart, Pie, Cell,
} from "recharts";

function App() {
  const [data, setData] = useState({ temperature: 0, humidity: 0 });
  const [history, setHistory] = useState([]); // stores recent data points for charts
  const [error, setError] = useState(null);

  // Keep a max history of 10 points
  const MAX_HISTORY = 10;

  useEffect(() => {
    const fetchData = async () => {
      try {
        const res = await fetch("http://localhost:8000/device/latest");
        if (!res.ok) throw new Error("Network response was not ok");
        const json = await res.json();

        // Update latest data
        setData(json);
        setError(null);

        // Add timestamp for charting
        const timestamp = new Date().toLocaleTimeString();

        // Append new data point to history, keep max MAX_HISTORY length
        setHistory((prev) => {
          const newHistory = [...prev, { time: timestamp, ...json }];
          if (newHistory.length > MAX_HISTORY) {
            newHistory.shift();
          }
          return newHistory;
        });
      } catch (err) {
        setError("Failed to fetch data from backend");
        console.error(err);
      }
    };

    fetchData();
    const interval = setInterval(fetchData, 5000);
    return () => clearInterval(interval);
  }, []);

  // Data for PieChart (snapshot distribution)
  const pieData = [
    { name: "Temperature", value: data.temperature || 0, fill: "#8884d8" },
    { name: "Humidity", value: data.humidity || 0, fill: "#82ca9d" },
  ];

  return (
    <>
      <About />
      <div style={{ padding: 30, fontFamily: "Arial", maxWidth: 900, margin: "auto" }}>
        <h2>Live IoT Dashboard</h2>
        {error ? (
          <p style={{ color: "red" }}>{error}</p>
        ) : (
          <>
            <p><strong>Temperature:</strong> {data.temperature} °C</p>
            <p><strong>Humidity:</strong> {data.humidity} %</p>

            {/* LineChart: Trends over time */}
            <h3>Temperature & Humidity Trends</h3>
            <LineChart
              width={850}
              height={300}
              data={history}
              margin={{ top: 5, right: 30, left: 20, bottom: 5 }}
            >
              <CartesianGrid strokeDasharray="3 3" />
              <XAxis dataKey="time" />
              <YAxis />
              <Tooltip />
              <Legend />
              <Line type="monotone" dataKey="temperature" stroke="#8884d8" />
              <Line type="monotone" dataKey="humidity" stroke="#82ca9d" />
            </LineChart>

            {/* AreaChart: Same trends but filled */}
            <h3>Variation Area Chart</h3>
            <AreaChart
              width={850}
              height={200}
              data={history}
              margin={{ top: 10, right: 30, left: 0, bottom: 0 }}
            >
              <defs>
                <linearGradient id="colorTemp" x1="0" y1="0" x2="0" y2="1">
                  <stop offset="5%" stopColor="#8884d8" stopOpacity={0.8} />
                  <stop offset="95%" stopColor="#8884d8" stopOpacity={0} />
                </linearGradient>
                <linearGradient id="colorHum" x1="0" y1="0" x2="0" y2="1">
                  <stop offset="5%" stopColor="#82ca9d" stopOpacity={0.8} />
                  <stop offset="95%" stopColor="#82ca9d" stopOpacity={0} />
                </linearGradient>
              </defs>
              <XAxis dataKey="time" />
              <YAxis />
              <CartesianGrid strokeDasharray="3 3" />
              <Tooltip />
              <Area
                type="monotone"
                dataKey="temperature"
                stroke="#8884d8"
                fillOpacity={1}
                fill="url(#colorTemp)"
              />
              <Area
                type="monotone"
                dataKey="humidity"
                stroke="#82ca9d"
                fillOpacity={1}
                fill="url(#colorHum)"
              />
            </AreaChart>

            {/* RadialBarChart: Current levels */}
            <h3>Current Levels Gauge</h3>
            <RadialBarChart
              width={400}
              height={250}
              cx="50%"
              cy="50%"
              innerRadius="20%"
              outerRadius="90%"
              barSize={15}
              data={[
                { name: "Temperature", value: data.temperature || 0, fill: "#8884d8" },
                { name: "Humidity", value: data.humidity || 0, fill: "#82ca9d" },
              ]}
              startAngle={180}
              endAngle={0}
            >
              <RadialBar minAngle={15} label background clockWise dataKey="value" />
              <Legend
                iconSize={10}
                width={120}
                height={40}
                layout="horizontal"
                verticalAlign="bottom"
                align="center"
              />
              <Tooltip />
            </RadialBarChart>

            {/* PieChart: Snapshot comparison */}
            <h3>Snapshot: Temperature vs Humidity</h3>
            <PieChart width={400} height={300}>
              <Pie
                data={pieData}
                dataKey="value"
                nameKey="name"
                cx="50%"
                cy="50%"
                outerRadius={100}
                label
              >
                {pieData.map((entry, index) => (
                  <Cell key={`cell-${index}`} fill={entry.fill} />
                ))}
              </Pie>
              <Tooltip />
              <Legend />
            </PieChart>
          </>
        )}
      </div>
    </>
  );
}

export default App;
