import React, { useEffect, useState } from "react";

export default function About() {
  const [showDetails, setShowDetails] = useState(false);

  useEffect(() => {
    window.scrollTo({ top: 0, behavior: "smooth" });
  }, []);

  return (
    <div
      style={{
        maxWidth: 900,
        margin: "auto",
        padding: 30,
        fontFamily: "'Segoe UI', Tahoma, Geneva, Verdana, sans-serif",
        backgroundColor: "#f0f8ff",
        borderRadius: 12,
        boxShadow: "0 4px 12px rgba(0,0,0,0.1)",
      }}
    >
      <header
        style={{
          backgroundColor: "#0a192f",
          color: "#fff",
          padding: 30,
          textAlign: "center",
          borderRadius: "12px 12px 0 0",
          boxShadow: "0 4px 8px rgba(0,0,0,0.2)",
        }}
      >
        <h1
          style={{
            fontSize: "2.8rem",
            letterSpacing: "2px",
            animation: "fadeInDown 1s ease",
            marginBottom: 8,
          }}
        >
          Cloud Native IoT Device Management
        </h1>
        <h3 style={{ fontWeight: 400, opacity: 0.8 }}>
          Mini Project by CSE - Q Team 7, Anurag University
        </h3>
      </header>

      <section
        style={{
          marginTop: 40,
          padding: 20,
          backgroundColor: "#e3f2fd",
          borderRadius: 8,
          cursor: "pointer",
          transition: "background-color 0.3s ease",
          userSelect: "none",
        }}
        onClick={() => setShowDetails(!showDetails)}
        onMouseEnter={(e) => (e.currentTarget.style.backgroundColor = "#bbdefb")}
        onMouseLeave={(e) => (e.currentTarget.style.backgroundColor = "#e3f2fd")}
        aria-expanded={showDetails}
        aria-controls="about-details"
      >
        <h2
          style={{
            color: "#0a192f",
            marginBottom: 12,
            display: "flex",
            alignItems: "center",
            justifyContent: "space-between",
          }}
        >
          About the Project
          <span
            style={{
              fontSize: 24,
              display: "inline-block",
              transition: "transform 0.3s ease",
              transform: showDetails ? "rotate(180deg)" : "rotate(0deg)",
              marginLeft: 12,
            }}
            aria-label={showDetails ? "Collapse details" : "Expand details"}
          >
            ▼
          </span>
        </h2>

        <div
          id="about-details"
          style={{
            maxHeight: showDetails ? "300px" : "0px",
            overflow: "hidden",
            transition: "max-height 0.5s ease",
          }}
        >
          <p
            style={{
              fontSize: 16,
              lineHeight: 1.6,
              color: "#102027",
              marginTop: showDetails ? 12 : 0,
            }}
          >
            {showDetails
              ? `This project demonstrates a cloud-native solution to manage and monitor
                Internet of Things (IoT) devices in real-time. Using a modern tech stack,
                including MQTT for device communication, Docker for containerization,
                and FastAPI for backend APIs, it offers a seamless user experience.
                The system integrates device simulators, live dashboards, and scalable
                microservices — all crafted by the talented CSE - Q Team 7 at Anurag University.`
              : "Click here to learn more about the project details and technologies used."}
          </p>
        </div>
      </section>

      <section style={{ marginTop: 30 }}>
        <h2 style={{ color: "#0a192f" }}>Team Contributions</h2>
        <ul style={{ fontSize: 16, color: "#333", lineHeight: 1.5 }}>
          <li>☁️ Developed scalable cloud-native microservices architecture.</li>
          <li>📡 Integrated real-time IoT device simulation using MQTT protocol.</li>
          <li>📊 Created a dynamic dashboard with live charts for monitoring.</li>
          <li>🤝 Ensured modular, maintainable code with team collaboration.</li>
          <li>🎓 Tailored solution for academic and research use at Anurag University.</li>
        </ul>
      </section>

      <section
        style={{
          marginTop: 40,
          backgroundColor: "#e1f5fe",
          padding: 20,
          borderLeft: "6px solid #0288d1",
          borderRadius: 6,
          fontWeight: "bold",
          color: "#01579b",
        }}
      >
        <h3>🔑 Key Highlights</h3>
        <ul style={{ marginTop: 10, lineHeight: 1.5 }}>
          <li
            style={{ position: "relative", cursor: "help" }}
            title="Runs inside Docker containers for easy deployment."
          >
            🌐 Fully Cloud-Native with Docker Container Support
          </li>
          <li
            style={{ position: "relative", cursor: "help" }}
            title="Uses MQTT protocol to stream real-time device data."
          >
            📡 Real-Time Device Data Streaming via MQTT
          </li>
          <li
            style={{ position: "relative", cursor: "help" }}
            title="Interactive web dashboard with live updating charts and controls."
          >
            📊 Interactive Web Dashboard with Live Updating Charts
          </li>
          <li
            style={{ position: "relative", cursor: "help" }}
            title="Modular and scalable codebase to easily add new features."
          >
            🧩 Modular & Scalable Codebase for Easy Expansion
          </li>
          <li
            style={{ position: "relative", cursor: "help" }}
            title="Customized for Anurag University's academic and research needs."
          >
            🏫 Customized for Anurag University's Academic Needs
          </li>
        </ul>
      </section>

      <footer
        style={{
          marginTop: 40,
          textAlign: "center",
          color: "#444",
        }}
      >
        <p>
          <strong>Submitted by:</strong> CSE - Q Team 7, Anurag University
        </p>
        <small>© 2025 All rights reserved.</small>
      </footer>

      {/* CSS animation */}
      <style>{`
        @keyframes fadeInDown {
          0% {
            opacity: 0;
            transform: translateY(-20px);
          }
          100% {
            opacity: 1;
            transform: translateY(0);
          }
        }
      `}</style>
    </div>
  );
}
