import React, { useEffect, useState } from "react";
import {
  LineChart, Line, XAxis, YAxis, CartesianGrid, Tooltip, Legend,
  RadialBarChart, RadialBar, PieChart, Pie, Cell,
} from "recharts";
import { useNavigate } from "react-router-dom";

export default function Dashboard() {
  const navigate = useNavigate();
  const [latest, setLatest] = useState({ temperature: 0, humidity: 0 });
  const [history, setHistory] = useState([]);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchLatest = async () => {
      try {
        const res = await fetch("http://localhost:8000/device/latest");
        const json = await res.json();
        setLatest(json);
      } catch (err) {
        setError("⚠️ Unable to fetch latest data.");
        console.error(err);
      }
    };
    fetchLatest();
    const interval = setInterval(fetchLatest, 5000);
    return () => clearInterval(interval);
  }, []);

  useEffect(() => {
    const fetchHistory = async () => {
      try {
        const res = await fetch("http://localhost:8000/device/history");
        const json = await res.json();
        const now = new Date();
        const timed = json.map((item, idx) => {
          const time = new Date(now - (json.length - idx - 1) * 5000);
          return { ...item, time: time.toLocaleTimeString() };
        });
        setHistory(timed);
      } catch (err) {
        setError("⚠️ Unable to fetch historical data.");
        console.error(err);
      }
    };
    fetchHistory();
    const interval = setInterval(fetchHistory, 15000);
    return () => clearInterval(interval);
  }, []);

  const pieData = [
    { name: "Temperature", value: latest.temperature, fill: "#8884d8" },
    { name: "Humidity", value: latest.humidity, fill: "#82ca9d" },
  ];

  return (
    <div style={{ padding: 30, fontFamily: "Arial", maxWidth: 1000, margin: "auto" }}>
      <h2>📡 IoT Device Live Dashboard</h2>
      <button
        onClick={() => navigate("/")}
        style={{
          marginBottom: 20,
          padding: "10px 20px",
          backgroundColor: "#0a192f",
          color: "white",
          border: "none",
          borderRadius: 5,
          cursor: "pointer",
        }}
      >
        ← Back to About
      </button>

      {error ? (
        <p style={{ color: "red" }}>{error}</p>
      ) : (
        <>
          <p><strong>Temperature:</strong> {latest.temperature} °C</p>
          <p><strong>Humidity:</strong> {latest.humidity} %</p>

          <h3>📈 Trends (Last 1.5 minutes)</h3>
          <LineChart width={900} height={300} data={history}>
            <CartesianGrid strokeDasharray="3 3" />
            <XAxis dataKey="time" />
            <YAxis />
            <Tooltip />
            <Legend />
            <Line type="monotone" dataKey="temperature" stroke="#8884d8" />
            <Line type="monotone" dataKey="humidity" stroke="#82ca9d" />
          </LineChart>

          <h3>🎯 Real-Time Sensor Gauge</h3>
          <RadialBarChart
            width={400}
            height={250}
            cx="50%" cy="50%"
            innerRadius="20%" outerRadius="90%"
            barSize={15}
            data={pieData}
            startAngle={180} endAngle={0}
          >
            <RadialBar background dataKey="value" />
            <Legend iconSize={10} layout="horizontal" verticalAlign="bottom" align="center" />
            <Tooltip />
          </RadialBarChart>

          <h3>📊 Snapshot Comparison</h3>
          <PieChart width={400} height={300}>
            <Pie
              data={pieData}
              dataKey="value"
              nameKey="name"
              cx="50%" cy="50%"
              outerRadius={100}
              label
            >
              {pieData.map((entry, index) => (
                <Cell key={`cell-${index}`} fill={entry.fill} />
              ))}
            </Pie>
            <Tooltip />
            <Legend />
          </PieChart>
        </>
      )}
    </div>
  );
}
