import React, { useEffect, useState } from 'react';
import About from './About';
import {
  LineChart, Line, XAxis, YAxis, CartesianGrid, Tooltip, Legend,
  PieChart, Pie, Cell,
} from 'recharts';

const COLORS = ['#0088FE', '#00C49F'];

function App() {
  const [data, setData] = useState({ temperature: 0, humidity: 0 });
  const [history, setHistory] = useState([]);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const res = await fetch('http://localhost:8000/device/latest');
        if (!res.ok) throw new Error('Network response was not ok');
        const json = await res.json();
        setData(json);
        setError(null);

        // Keep last 10 readings in history for chart
        setHistory((oldHistory) => {
          const newHistory = [...oldHistory, { 
            time: new Date().toLocaleTimeString(), 
            temperature: json.temperature, 
            humidity: json.humidity 
          }];
          if (newHistory.length > 10) newHistory.shift(); // keep max 10
          return newHistory;
        });

      } catch (err) {
        setError('Failed to fetch data from backend');
        console.error(err);
      }
    };

    fetchData();
    const interval = setInterval(fetchData, 5000);
    return () => clearInterval(interval);
  }, []);

  const pieData = [
    { name: 'Temperature', value: data.temperature || 0 },
    { name: 'Humidity', value: data.humidity || 0 },
  ];

  return (
    <>
      <About />

      <div style={{ padding: 30, fontFamily: 'Arial', maxWidth: 900, margin: 'auto' }}>
        <h2>Live IoT Dashboard</h2>

        {error ? (
          <p style={{ color: 'red' }}>{error}</p>
        ) : (
          <>
            <p><strong>Temperature:</strong> {data.temperature} Â°C</p>
            <p><strong>Humidity:</strong> {data.humidity} %</p>

            <h3>Recent Data Over Time</h3>
            <LineChart
              width={800}
              height={300}
              data={history}
              margin={{ top: 5, right: 20, left: 10, bottom: 5 }}
            >
              <CartesianGrid stroke="#ccc" strokeDasharray="5 5" />
              <XAxis dataKey="time" />
              <YAxis />
              <Tooltip />
              <Legend />
              <Line type="monotone" dataKey="temperature" stroke="#0088FE" />
              <Line type="monotone" dataKey="humidity" stroke="#00C49F" />
            </LineChart>

            <h3>Current Data Proportions</h3>
            <PieChart width={400} height={300}>
              <Pie
                data={pieData}
                cx="50%"
                cy="50%"
                outerRadius={80}
                fill="#8884d8"
                dataKey="value"
                label
              >
                {pieData.map((entry, index) => (
                  <Cell key={`cell-${index}`} fill={COLORS[index % COLORS.length]} />
                ))}
              </Pie>
              <Tooltip />
              <Legend />
            </PieChart>
          </>
        )}
      </div>
    </>
  );
}

export default App;
